$months = 2; $years = 1230..1240;$x = 0; foreach ($year in $years){ foreach ($month in $months) {$daysinmonth = [system.datetime]::DaysInMonth($year, $month); $color = "red", "green", "yellow" ; if ($x -lt 2){ $x += 1} else {$x = 0};$monthname = (get-culture).DateTimeFormat.GetMonthName($month) ;write-host "in $($year) and $($monthname) there are $($daysinmonth) days for that month" -ForegroundColor $color[$x]}}